<?php
class Config {
	public $urls;
	public $logo;
	private static $instance;
	
	private function __construct() {
		$this->urls = array(); 
		$this->urls['loginUrl'] = "?r=user/login";
		$this->urls['indexUrl'] = "?r=site/index";
		$this->urls['homeUrl'] = "?r=site/index";
		$this->urls['logoutUrl'] = "?r=user/logout";
		$this->urls['signupUrl'] = "?r=user/signup";
		$this->logo = "data:image/png;base64,"; 
	}
	
	public static function getUrl($key) {
		$urls = self::getInstance()->urls;
		if (isset($urls[$key])) {
			return $urls[$key];
		}
		return "";
	}
	
	public static function getInstance(){ 
		if (!isset(self::$instance)) {
			self::$instance = new Config();
		}
		return self::$instance;
	}
}
