<?php
class Config {
	public $urls;
	public $logo;
	private static $instance;
	
	private function __construct() {
		$this->urls = array(); 
		$this->urls['loginUrl'] = "?r=user/login";
		$this->urls['indexUrl'] = "?r=site/index";
		$this->urls['homeUrl'] = "?r=site/index";
		$this->urls['logoutUrl'] = "?r=user/logout";
		$this->urls['signupUrl'] = "?r=user/signup";
		$this->urls['forgetUrl'] = "?r=user/forget";
		$this->urls['verifyUrl'] = "?r=user/verify";
		
		// 最长校验时限，超过此时间会删除校验码, (秒)
		$this->maxVerifyTime = 24 * 3600; // 1天
		// http://www.scriptol.com/html5/canvas/logo.php , text=#ffffff, bord=#aaaaa, back=#ffffff, transparent background, comic sans MS, border.thickness=1,border.offset=1,shadow=1,1
		$this->logo = "data:image/png;base64,";
		// 250*60, x=1, y=50, Blod, border.offset=2, shadow.offset=1,shadow.blur=10
		$this->biglogo = "data:image/png;base64,";
	}
	
	public static function getUrl($key) {
		$urls = self::getInstance()->urls;
		if (isset($urls[$key])) {
			return $urls[$key];
		}
		return "";
	}
	
	public static function getInstance(){ 
		if (!isset(self::$instance)) {
			self::$instance = new Config();
		}
		return self::$instance;
	}
}
